export default {
    props: ['service'],
    data(){
        return{
            editDialog: false,
            editedTitle: this.service.title,
            editedDescription: this.service.description,
            editedPrice: this.service.price
        }
    },
    methods: {
        OnSaveChanges(){
            if(this.editedTitle.trim() === '' || this.editedDescription.trim() === '' || this.editedPrice.trim() === ''){
                return
            }
            this.editDialog = false;
            this.$store.dispatch('updateServiceData', {
                id: this.service.id,
                title: this.editedTitle,
                description: this.editedDescription,
                price: this.editedPrice
            })
        }
    }
}
